import axios from "axios";
const API_URL = "http://localhost:8000/api/auth/login/"
export const login = async(email, password) => {
    return axios
      .post(API_URL, {
        email,
        password,
      })
      .then((response) => {
        if (response.data) {
          localStorage.setItem("user", JSON.stringify(response.data));
        }
        return response.data;
      });
  };
