import axios from "axios";
const API_URL = "http://localhost:8000/api/auth/login/"
export const login = async(data) => {
    return axios
      .post(API_URL, 
        data
      )
      .then((response) => {
        // if (response.data) {
        //   localStorage.setItem("user", JSON.stringify(response.data));
        // }
        return response.data;
      });
  };
